import React from 'react';
import { 
  StyleSheet,
  Text,
  View
} from 'react-native';
import { useQuery } from '@apollo/client';
import { fetchRestaurantDetails } from './queries';
import OrdersView from './OrdersView';
import { ScrollView } from 'react-native-gesture-handler';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  },

  scrollView: {
    width: '100%',
    flex: 1,
    paddingLeft: 25,
    paddingRight: 25,
    paddingTop: 14
  },

  title: {
    fontFamily: 'SulSans-Regular',
    color: 'rgb(62, 62, 62)',
    fontSize: 14
  }
});

const RestaurantDetails = ({route, navigation} : any) => {
  const restaurant = route.params.restaurant;
  const {data, error} = useQuery(fetchRestaurantDetails, {
    variables: {
      restaurantId: restaurant.id
    }
  });

  return (
    <View style={styles.container}>
      <ScrollView style={styles.scrollView}>
        <Text>{data?.fetchRestaurantDetails?.name}</Text>

        <OrdersView
          title='Menu'
          items={data?.fetchRestaurantDetails?.orders}
          navigation={navigation} />
      </ScrollView>
    </View>
  )
}

export default RestaurantDetails;
