import React from 'react';
import { 
  StyleSheet, 
  View,
  Image
} from 'react-native';
import Carousel from 'react-native-snap-carousel';

const styles = StyleSheet.create({
  image: {
    width: 267,
    height: 140
  }
});

const Slider = () => {
  var ref = React.createRef<any>();

  const itemList = [
    {
      imageSrc: require('../../../assets/images/highlights/70off.png'),
      component: null
    },

    {
      imageSrc: require('../../../assets/images/highlights/acaiesorvete.png'),
      component: null
    },

    {
      imageSrc: require('../../../assets/images/highlights/entregagratis.png'),
      component: null
    },

    {
      imageSrc: require('../../../assets/images/highlights/maispedidos.png'),
      component: null
    }
  ];

  const handleRender = ({ item, index }: { item: any, index: number }) => {
    return (
      <Image style={styles.image}
             source={item.imageSrc} />
    )
  }

  return (
    <Carousel layout={'default'}
              sliderWidth={300}
              ref={ref}
              data={itemList}
              itemWidth={267}
              itemHeight={140}
              renderItem={handleRender} />
  );
}

export default Slider;
