import * as Location from 'expo-location';
import GoogleApi from './settings';

export default class GeolocatorService {
  static apiKey = '1a0f0a33030643ef97bfd6fc409f6b2a';
  static getLocation = async () => {
    const location = await Location.getCurrentPositionAsync({});

    if(location) {
      const url = `https://api.opencagedata.com/geocode/v1/json?key=${GeolocatorService.apiKey}&q=51.952659%2C+7.632473&pretty=1&no_annotations=1`;
      fetch('https://maps.googleapis.com/maps/api/geocode/json?address=' + location.coords.latitude + ',' + location.coords.longitude + '&key=' + GoogleApi.apiKey)
        .then((response) => response.json())
        .then((responseJson) => {
            console.log('ADDRESS GEOCODE is BACK!! => ' + JSON.stringify(responseJson));
      })
    }

    return 'blank';
  }
}
