import React from 'react';
import { 
  StyleSheet, 
  ScrollView,
  View,
  Image,
  Animated,
  Dimensions
} from 'react-native';

const useStyles = (props: any) => StyleSheet.create({
  container: {
    display: 'flex',
    justifyContent: 'center', 
    alignItems: 'center',
    flexDirection: 'column'
  },

  inner: {
    width: props.width,
    height: props.width
  },

  image: {
    width: 267,
    height: 140,
    marginRight: 10
  },

  dotsContainer: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'row'
  },

  dots: {
    height: 8, 
    width: 8, 
    backgroundColor: '#000000', 
    margin: 4, 
    borderRadius: 8
  }
})

const Slider = () => {
  const {width} = Dimensions.get('window');
  const scrollX = new Animated.Value(0);
  const position = Animated.divide(scrollX, width);

  const styles = useStyles({width: width});

  const itemList = [
    {
      imageSrc: require('../../../assets/images/highlights/70off.png'),
      component: null
    },

    {
      imageSrc: require('../../../assets/images/highlights/acaiesorvete.png'),
      component: null
    },

    {
      imageSrc: require('../../../assets/images/highlights/entregagratis.png'),
      component: null
    },

    {
      imageSrc: require('../../../assets/images/highlights/maispedidos.png'),
      component: null
    }
  ];

  return (
      <ScrollView
        horizontal={true}
        pagingEnabled={true} 
        showsHorizontalScrollIndicator={false}
        scrollEventThrottle={16} >
        {itemList.map((item, index) => { 
          return ( 
            <Image key={index} 
                    style={styles.image}
                    source={item.imageSrc} />
          );
        })}
      </ScrollView>
  );
}

export default Slider;
