import React, { 
  useState,
  useEffect 
} from 'react';
import { RecoilRoot, useRecoilState } from 'recoil';
import { Provider as PaperProvider } from 'react-native-paper';

import { StyleSheet, Text, View } from 'react-native';

import loadFontsAsync from './src/theme/fonts';
import BottomNavigationBar from './src/screens/navigation';
import GeolocatorService from './src/geolocator';

import * as atoms from './src/geolocator/atom';

const App = () => {
  const [isReady, setReady] = useState(false);
  const [, setAddress] = useRecoilState(atoms.addressState);

  useEffect(() => {
    (async () => {
      await loadFontsAsync().then(() => {
        setReady(true);
      });

      await GeolocatorService
        .getLocation()
        .then((value) => {
          setAddress(value);
        });
    })(); 
  }, []);

  return (
    <>
      {isReady === true && (
        <RecoilRoot>
          <PaperProvider>
            <BottomNavigationBar />
          </PaperProvider>
        </RecoilRoot>
      )}
    </>
  )
}

export default App;
