import React from 'react';
import { 
  StyleSheet, 
  Text, 
  View,
  ScrollView 
} from 'react-native';
import AppBar from './AppBar';
import CupomComponent from '../../components/Cupom';
import Category from '../../models/Category/model';
import CategoryComponent from '../../components/Category';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  },

  title: {
    fontFamily: 'SulSans-Bold',
    fontSize: 40,
    color: '#000000',
    marginTop: 10
  },

  scrollView: {
    paddingLeft: 20,
    paddingRight: 20,
    paddingTop: 14,
  },

  aligner: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column'
  },

  categoryAligner: {
    marginTop: 14
  }
});

const HomeScreen = () => {
  const categoryList = [
    new Category(
      1,
      'Mercado',
      '../../../assets/images/categories/mercado.png'
    )
  ];
  return (
    <View style={styles.container}>
      <AppBar />

      <ScrollView style={styles.scrollView}>
        <View style={styles.aligner}>
          <CupomComponent value={8} />

          <ScrollView horizontal={true} 
                      style={styles.categoryAligner}>
            {categoryList.map(category => (
              <CategoryComponent category={category}></CategoryComponent>
            ))
              
            }
          </ScrollView>

          <Text style={styles.title}>iFood, homescreen '-'</Text>
        </View>
      </ScrollView>
    </View>
  );
}

export default HomeScreen;
