import React from 'react';
import { 
  StyleSheet,
  Text
} from 'react-native';
import { useQuery } from '@apollo/client';
import { fetchRestaurantDetails } from './queries';

const styles = StyleSheet.create({
  container: {
    elevation: 0,
    shadowRadius: 0,
    shadowOpacity: 0,
    shadowOffset: {
      height: 0,
      width:0
    },
    borderBottomColor: 'transparent',
    borderBottomWidth: 0
  },

  title: {
    fontFamily: 'SulSans-Regular',
    color: 'rgb(62, 62, 62)',
    fontSize: 14
  }
});

const RestaurantDetails = ({route, navigation} : any) => {
  const restaurant = route.params.restaurant;
  const {data, error} = useQuery(fetchRestaurantDetails, {
    variables: {
      restaurantId: restaurant.id
    }
  });

  return (
    <>
      <Text>{restaurant.name}{restaurant.name}</Text>
    </>
  )
}

export default RestaurantDetails;
